package id.ac.unpas.aplikasipenjadwalan.adapterimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.TextViewimport androidx.recyclerview.widget.RecyclerViewimport id.ac.unpas.aplikasipenjadwalan.Rimport id.ac.unpas.aplikasipenjadwalan.data.entity.Userclass UserAdapter(var list: List<User>) : RecyclerView.Adapter<UserAdapter.ViewHolder>() {    private lateinit var dialog: Dialog    fun setDialog(dialog: Dialog) {        this.dialog = dialog    }    interface Dialog{        fun onClick(position: Int)    }    inner class ViewHolder(view: View) : RecyclerView.ViewHolder(view) {        var mata_kuliah: TextView        var nama_dosen: TextView        var waktu: TextView        var ruangan: TextView        init {            mata_kuliah = view.findViewById(R.id.mata_kuliah)            nama_dosen = view.findViewById(R.id.nama_dosen)            waktu = view.findViewById(R.id.waktu)            ruangan = view.findViewById(R.id.ruangan)            view.setOnClickListener{                dialog.onClick(layoutPosition)            }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {        val view = LayoutInflater.from(parent.context).inflate(R.layout.row_user, parent, false)        return ViewHolder(view)    }    override fun getItemCount(): Int {        return list.size    }    override fun onBindViewHolder(holder: ViewHolder, position: Int) {        holder.mata_kuliah.text = list[position].mata_kuliah        holder.nama_dosen.text = list[position].nama_dosen        holder.waktu.text = list[position].waktu        holder.ruangan.text = list[position].ruangan    }}