package id.ac.unpas.aplikasipenjadwalan.data.daoimport androidx.room.Daoimport androidx.room.Deleteimport androidx.room.Insertimport androidx.room.Queryimport androidx.room.Updateimport id.ac.unpas.aplikasipenjadwalan.data.entity.User@Daointerface UserDao {    @Query("SELECT * FROM users")    fun getAll(): List<User>    @Query("SELECT * FROM users WHERE uid IN (:userIds)")    fun loadAllByIds(userIds: IntArray): List<User>    @Insert    fun insertAll(vararg users: User)    @Delete    fun delete(user: User)    @Query("SELECT * FROM users WHERE uid = :userId")    fun loadById(userId: Int): User?    @Query("SELECT * FROM users WHERE uid = :uid")    fun get(uid: Int) : User    @Update    fun update(user: User)}