package id.ac.unpas.aplikasipenjadwalan.data.entityimport androidx.room.ColumnInfoimport androidx.room.Entityimport androidx.room.PrimaryKey@Entity(tableName = "users")class User {    @PrimaryKey(autoGenerate = true)    var uid: Int = 0 // Pastikan tipe data dan inisialisasi kunci utama sesuai    @ColumnInfo(name = "mata_kuliah")    var mata_kuliah: String? = null // Berikan nilai awal atau tambahkan tanda tanya (?) jika nullable    @ColumnInfo(name = "nama_dosen")    var nama_dosen: String? = null // Berikan nilai awal atau tambahkan tanda tanya (?) jika nullable    @ColumnInfo(name = "waktu")    var waktu: String? = null // Berikan nilai awal atau tambahkan tanda tanya (?) jika nullable    @ColumnInfo(name = "ruangan")    var ruangan: String? = null // Berikan nilai awal atau tambahkan tanda tanya (?) jika nullable}